$(".buttonAnswerPlus").one("click",function(){var t=parseFloat($(this).next("div").text()),a=t+1;$(this).next("div").text(a);var r=$(this).attr("data-answer-id");$.ajax({method:"POST",url:"/answers/rating",data:{answerId:r,rating:a}})}),$(".buttonAnswerMinus").one("click",function(){var t=parseFloat($(this).prev("div").text()),a=t-1;$(this).prev("div").text(a);var r=$(this).attr("data-answer-id");$.ajax({method:"POST",url:"/answers/rating",data:{answerId:r,rating:a}})});